import React, {useState, useContext, useEffect} from "react";
import ReactDOM from "react-dom";
import { createBrowserHistory } from "history";
import { BrowserRouter, Router,  Route, Switch, Redirect } from "react-router-dom";

import AuthLayout from "layouts/Auth.js";
import RtlLayout from "layouts/RTL.js";
import AdminLayout from "layouts/Admin.js";
import "assets/scss/material-dashboard-pro-react.scss?v=1.8.0";
import MainContext from './MainContext';
import { withRouter } from 'react-router-dom';


export default function App_Route(props) {

    const hist = createBrowserHistory();
    //const token = React.useContext(MainContext);
    const token = props.value;
    console.log("token:"+token);
    const [loginState, setLoginState] = useState(false);
    console.log(loginState);
    useEffect(()=>{
        if(token===""){
            setLoginState(true);
            console.log("set_login_state_1111if:"+loginState);
            //props.history.push('/auth/login-page');
        } else {
            setLoginState(false)
            console.log("set_login_state_2222else:"+loginState);           
        }
    }, [token])

    return (<React.Fragment> 
                {
                    <BrowserRouter history={hist}>
                        <div>
                            {loginState ? (
                                <Redirect from="/" to="/auth/login-page" />
                            ) : (
                                <Switch>
                                    <Route path="/rtl" component={RtlLayout} />
                                    <Route path="/auth" component={AuthLayout} />
                                    <Route path="/admin" component={AdminLayout} />
                                    <Redirect from="/" to="/admin/dashboard" />
                                </Switch>
                            )}

                        </div>
                    </BrowserRouter>
                }
            </React.Fragment>
    )
}
