import React,  {useState, useEffect} from "react";
import ReactDOM from "react-dom";
import { createBrowserHistory } from "history";
import { Router, Route, Switch, Redirect, withRouter } from "react-router-dom";
import axios from 'axios';

import AuthLayout from "layouts/Auth.js";
import RtlLayout from "layouts/RTL.js";
import AdminLayout from "layouts/Admin.js";

import "assets/scss/material-dashboard-pro-react.scss?v=1.8.0";
import { createContext, useContext } from 'react';
import io from 'socket.io-client/dist/socket.io';
import {API_BASE_URL} from "assets/jss/material-dashboard-pro-react";

import PrivateRoute from './PrivateRoute';



export default function App() {

    const hist = createBrowserHistory();

    function wait(ms){
      var start = new Date().getTime();
      var end = start;
      while(end < start + ms) {
        end = new Date().getTime();
        //console.log(end);
      };
    };

    function gonder(){
      var msg = {
          type: "send_location",
          latitude: "12.4455666",
          longtitude:"45.3322222",
          rid:"2"
        };

       if (ws.readyState === WebSocket.OPEN) {
           ws.send(JSON.stringify(msg));
       }
    }


    async function retrieveToken(token) {
      try{
            const urlx = `${API_BASE_URL}api/refresh/`;
            const header = {Authorization: 'Bearer ' + token};
            console.log(token);
            console.log(urlx);
            //console.log(header)
            var result = await axios({
                method: 'post',
                url: urlx,
                data: { "refresh": token },
                //headers: {"Authorization" : `Bearer ${JWTToken}`},
                });
            console.log(result);
            //return;
        }
          catch (err) {
              console.log("----");
              console.log(err.response);
              //return;
          };
      };


        async function getData (token) {
              console.log(token);
              const urlx = `${API_BASE_URL}api/refresh/`;
              console.log(urlx);
              try {
                //const data = await fetch(urlx, { headers: {"Authorization" : 'Bearer ' + token} });
                const data = await post(urlx, { "refresh": token });
                console.log(JSON.stringify(data)); // JSON-string from `response.json()` call
              } catch (error) {
                console.error(error);
              }
          };


      const AuthContext = createContext();
      //const token = localStorage.getItem('token');
      const token = "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoicmVmcmVzaCIsImV4cCI6MTU3NTAxODQzOSwianRpIjoiMGU1MWNkOGUyNjU1NDQ1OThhNzc2MmM2ZDNkYjBhZGMiLCJ1c2VyX2lkIjoyfQ.td3HDkunwe_wY3iGBgjT3t1sYFjAsM_KntZ3JkNCgFY";
      // Retrieve your token on client side
      let newToken = retrieveToken(token)
      //let newToken = getData(token);
      console.log(newToken);
      let endpoint = "ws://127.0.0.1:8000/kurye/"

      // Create new WebSocket
      let ws = new WebSocket(endpoint + "?token=" + newToken)

      //console.log("App içinden token:"+token);
      //const path = `ws://127.0.0.1:8000/kurye/${token}/`;

      //const ws = new WebSocket(path);
      console.log("state of ws-1 ");
      console.log(ws.readyState);

      ws.onopen = () => {
       console.log("WebSocket now open...");
       console.log(ws.readyState);
       const sayi = 60;
       const i = 0;
       while (i < sayi) {
         gonder();
         wait(5000);
         i = i + 1;
       }
      };
      console.log("state of ws-3");
      console.log(ws.readyState);

      ws.onclose = () => {
            console.log("WebSocket now closed...");
            console.log(ws.readyState);
      };






    return (
        <AuthContext.Provider value={token}>
            <Router history={hist}>
                <Switch>
                    { (token == "") && <Route path="/auth" component={AuthLayout} />}
                    <Route path="/rtl" component={RtlLayout} />
                    <Route path="/auth" component={AuthLayout} />
                    <Route path="/admin" component={AdminLayout} />
                    <Redirect from="/" to="/admin/dashboard" />
                </Switch>
            </Router>
        </AuthContext.Provider>
    )
}
